<?php

$input= "";


function check($input) {
    for($i = 3; $i< strlen($input); $i++) {
        if($input[$i] == $input[$i-1] || 
            $input[$i-1] == $input[$i-2] || 
            $input[$i-2] == $input[$i-3] || 
            $input[$i-1]== $input[$i-3] || 
            $input[$i] == $input[$i-2] || 
            $input[$i] == $input[$i-3]) {
            // on avance l'intervalle d'un cran
            echo $i . " :" . $input[$i] . PHP_EOL . 'on avance' . PHP_EOL;
        }
        else {
            echo "After char " . $i + 1 . " :" . $input[$i] . PHP_EOL;;            
            break;
        }
    }
}
// print_r(check($input));

function checkPartTwo($input) {
    for($i = 13; $i< strlen($input); $i++) {
        $stringInterval = substr($input, $i- 13, 14);
        $countChar = count_chars($stringInterval, 1);
        $doubleLetters = array_filter($countChar, fn($count) => $count > 1);
        if(count($doubleLetters) === 0) {
            echo "After char " . $i + 1 . " :" . $input[$i] . PHP_EOL;;            
            break;
        }
    }
}

print_r(checkPartTwo($input));

